import {useState} from 'react'

const yi = (state)=>{
    const reflashQueue = [];
    const stateProxy = new Proxy(state,{
        set(target,key,value){
            state[key] = value;
            const length = reflashQueue.length
            for(let i=0;i<length;i++){
                const [r,reflash] = reflashQueue.shift();
                reflash(r+1);
            }
            return true;
        }
    })
    return ()=>{
        reflashQueue.push(useState(0))
        return stateProxy
    }
}
const stateModel = yi({
    count : 0
})
function App() {
    const state = stateModel()
    return (
    <div>
        {state.count}
        <AddBtn/>
    </div>
    );
}
const AddBtn = ()=>{
    const state = stateModel()
    return <button onClick={()=>state.count++}>add count</button>
}


export default App;
