import {observe} from './utils';

const countState = observe({
  count : 0,
  setCount(){
    this.count ++;
  }
})

function App() {
  const c = countState()
  return (
    <div className="App">
      {c.count}
      <AddBtn/>
    </div>
  );
}

const AddBtn = ()=>{
  const c = countState()
  return <button onClick={()=>c.setCount()}> add </button>
} 

export default App;
