import {useState} from 'react'
function yi(state){
    return ()=>{
        const [r,reflash] = useState(0);
        return new Proxy(state,{
            set(target,key,value){
                state[key] = value;
                reflash(r + 1);
                return true;
            }
        })
    }
}
const stateModel = yi({
    count : 0
  })
  function App() {
      const state = stateModel()
      return (
      <div>
        {state.count}
        <button onClick={()=>state.count++}>add count</button>
      </div>
    );
  }


export default App;
