import {useState,useEffect} from 'react';
export const config = {
    funReg : /^set/,
    private: false
}
export const observe = (state,options = config ) =>{
    if(options.funReg === undefined) options.funReg = config.funReg;
    if(options.private === undefined) options.private = config.private;
    const reflashSet = new Set();
    const renderNode = ()=>{
        // const length = reflashQueue.length;
        console.log(reflashSet);
        const reflashs = [...reflashSet];
        reflashs.forEach(item=>item())
        reflashSet.clear();
    }
    const stateProxy = new Proxy(state,{
        get(target,key){
            if(key.match(options.funReg) && options.private) {
                const privateFun = (...arg)=>{
                    target[key].apply(new Proxy(state,{
                        set(target,key,value){
                            target[key] = value;
                            renderNode();
                            return true;
                        }
                    }),arg)
                }
               
                return privateFun;
            }
            return target[key]
        },
        set(target,key,value){
            if(options.private) return false;
            if(key.match(options.funReg)) return false;
            target[key] = value;
            renderNode()
            return true;
        }
    })
    return ()=>{
        const [r,reflash] = useState(0)
        const reRender = ()=> reflash(r+1);
        reflashSet.add(reRender)
        useEffect(()=>{
            return ()=>{
                reflashSet.delete(reRender);
            }
        })
        return stateProxy
    }
}
