import {useState} from 'react';

const yi = (state)=>{
    const reflashs = [];
    const stateProxy = new Proxy(state,{
        set(target,key,value){
            state[key] = value;
            reflashs.forEach(([reflash,setReflash])=>{
                setReflash(!reflash)
            })
            reflash[1](!reflash[0])
            return true;
        }
    })
    return ()=>{
        reflashs.push(useState(true))
        return stateProxy
    }
}
export default yi
