import {useState} from 'react';
export const config = {
    funReg : /^set/,
    private: true
}
export const observe = (state,options = config ) =>{
    const reflashQueue = [];
    const renderNode = ()=>{
        const length = reflashQueue.length;
        for(let i=0;i<length;i++){
            const [r,reflash] = reflashQueue.shift();
            reflash(r+1);
        }
    }
    const stateProxy = new Proxy(state,{
        get(target,key){
            if(key.match(options.funReg) && options.private) {
                function privateFun(){
                    target[key].call(target)
                }
                return privateFun;
            }
            return target[key]
        },
        set(target,key,value){
            if(key.match(options.funReg)) return false;
            target[key] = value;
            renderNode()
            return true;
        }
    })
    return ()=>{
        reflashQueue.push(useState(0))
        return stateProxy
    }
}
