import {useState} from 'react';

const yi = (state)=>{
    const reflashQueue = [];
    const stateProxy = new Proxy(state,{
        set(target,key,value){
            state[key] = value;
            console.log("数据设置");
            console.log(reflashQueue);
            const length = reflashQueue.length
            
            for(let i=0;i<length;i++){
                const [r,reflash] = reflashQueue.shift();
                reflash(false);
            }
            
            return true;
        }
    })
    return ()=>{
        reflashQueue.push(useState(true))
        return stateProxy
    }
}
export default yi
