import {observe} from '../index';

const countState = observe({
  count : 0
})

function App() {
  const c = countState()
  return (
    <div className="App">
      {c.count}
      <AddBtn/>
    </div>
  );
}

const AddBtn = ()=>{
  const c = countState()
  const onClick = ()=>{
    c.count =   c.count+ 1
  }
  return <button onClick={onClick}> add </button>
} 

export default App;
