import {useState} from 'react';

const yi = (state)=>{
    const reflashs = [];
    const stateProxy = new Proxy(state,{
        set(target,key,value){
            state[key] = value;
            console.log("数据设置");
            
            reflashs.forEach((reflash)=>{
                reflash[1](!reflash[0])
            })
            
            return true;
        }
    })
    return ()=>{
        reflashs.push(useState(true))
        return stateProxy
    }
}
export default yi
