import {useState} from 'react'
const yi = (state)=>{
    let reflashs;
    const stateProxy = new Proxy(state,{
        set(target,key,value){
            state[key] = value;
            const [r,reflash] = reflash;
            reflash(r + 1);
            return true;
        }
    })
    return ()=>{
        reflashs = useState(0)
        return stateProxy
    }
}
const stateModel = yi({
    count : 0
  })
function App() {
    const state = stateModel()
    return (
    <div>
        {state.count}
        <AddBtn/>
    </div>
    );
}
const AddBtn = ()=>{
    const state = stateModel()
    return <button onClick={()=>state.count++}>add count</button>
}


export default App;
